// 定义接口
class Observer {
public: virtual void update(int arg) = 0; virtual ~Observer() = 0;
}; class Subject {
public: virtual void addObserver(Observer*) = 0; virtual void removeObserver(Observer*) = 0; virtual void notifyObservers() = 0; virtual ~Subject() = 0;
}; // 实现
class ConcreteObserver : public Observer {
public: virtual void update(int arg) { cout << arg << endl; }
}; class ConcreteSubject : public Subject {
public: virtual void addObserver(Observer* observer) override { observers.push_back(observer); } virtual void removeObserver(Observer* observer) override { auto pos = find(observers.begin(), observers.end(), observer); if (pos != observers.end()) { observers.erase(pos); } } virtual void notifyObservers() override { for (auto observer : observers) { observer->update(0); } } private: vector<Observer*> observers;
};

