// 定义接口
class FlyBehavior {
public: virtual void fly() = 0;
}; class Duck {
private: FlyBehavior* flyBehavior; public: // 可变的部分拆分出来，将其委托给行为类 virtual void performFly() { flyBehavior->fly(); } // 不变的部分 virtual void swim() { cout << "all duck can swim" << endl; } Duck(FlyBehavior* flyBehavior) : flyBehavior(flyBehavior) {} // 使用 set 更改行为 void setFlyBehevior(FlyBehavior* flyBehavior) { this->flyBehavior = flyBehavior; }
}; // 实现接口
class FlyWithWings : public FlyBehavior {
public: virtual void fly() override { cout << "FlyWithWings" << endl; }
}; class CannotFly : public FlyBehavior {
public: virtual void fly() override { cout << "CannotFly" << endl; }
}; int main()
{ FlyBehavior* fly_with_wings = new FlyWithWings; Duck duck(fly_with_wings); duck.performFly(); FlyBehavior* cannot_fly = new CannotFly; duck.setFlyBehevior(cannot_fly); duck.performFly();
}

