public: using FlyBehavior = function<void()>; private: FlyBehavior flyBehavior; public: // 可变的部分拆分出来，将其委托给行为类 virtual void performFly() { flyBehavior(); } // 不变的部分 virtual void swim() { cout << "all duck can swim" << endl; } Duck(FlyBehavior flyBehavior) : flyBehavior(flyBehavior) {} // 使用 set 更改行为 void setFlyBehevior(FlyBehavior flyBehavior) { this->flyBehavior = flyBehavior; }
}; int main()
{ Duck duck([]() { cout << "FlyBehavior" << endl; }); duck.performFly();
}

